mtree(DB_PATH) if logger: logger(f&#34;Successfully deleted {DB_PATH}&#34;) except Exception as e: if logger: logger(f&#34;Failed to delete database directory {DB_PATH}. Reason: {e}&#34;) else: if logger: logger(f&#34;Database directory {DB_PATH} does not exist, skipping deletion.&#34;) def fetch_exploit_db_id(cve_id, logger=None): search_url = f&#34;[https://www.exploit-db.com/search?cve=](https://www.exploit-db.com/search?cve=){cve_id}&#34; headers = { &#34;Accept&#34;: &#34;application/json, text/javascript, */*; q=0.01&#34;, &#34;X-Requested-With&#34;: &#34;XMLHttpRequest&#34;, &#34;User-Agent&#34;: &#34;Mozilla/5.0 (Windows NT 10.0; Win64; x64) &#34; &#34;AppleWebKit/537.36 (KHTML, like Gecko) &#34; &#34;Chrome/115.0.0.0 Safari/537.36&#34;, &#34;Referer&#34;: &#34;[https://www.exploit-db.com/](https://www.exploit-db.com/)&#34;, } try: response = requests.get(search_url, headers=headers) response.raise_for_status() data = response.json() if data.get(&#34;recordsTotal&#34;, 0) &gt; 0 and data.get(&#34;data&#34;): exploit_ids = [item[&#34;id&#34;] for item in data[&#34;data&#34;]] if logger: logger(f&#34;Found Exploit-DB IDs {exploit_ids} for CVE {cve_id}&#34;) return exploit_ids else: if logger: logger(f&#34;No Exploit-DB entries found for CVE {cve_id}&#34;) return [] except Exception as e: if logger: logger(f&#34;Failed to fetch Exploit-DB ID: {e}&#34;) return [] def fetch_exploit_db_poc(exploit_id, logger=None): raw_url = f&#34;[https://www.exploit-db.com/raw/](https://www.exploit-db.com/raw/){exploit_id}&#34; headers = { &#34;User-Agent&#34;: &#34;Mozilla/5.0 (Windows NT 10.0; Win64; x64) &#34; &#34;AppleWebKit/537.36 (KHTML, like Gecko) &#34; &#34;Chrome/115.0.0.0 Safari/537.36&#34; } try: response = requests.get(raw_url, headers=headers) response.raise_for_status() if logger: logger(f&#34;Successfully fetched Exploit-DB PoC for ID {exploit_id}&#34;) return response.text except Exception as e: if logger: logger(f&#34;Failed to fetch Exploit-DB PoC: {e}&#34;) return None def fetch_and_save_cve(cve_id, nvd_api_key, logger=None): url = &#34;[https://services.nvd.nist.gov/rest/json/cves/2.0](https://services.nvd.nist.gov/rest/json/cves/2.0)&#34; params = {&#34;cveId&#34;: cve_id} headers = {&#34;apiKey&#34;: nvd_api_key} nvd_data = {} cve_info = None try: response = requests.get(url, headers=headers, params=params) response.raise_for_status() nvd_data = response.json() vulns = nvd_data.get(&#34;vulnerabilities&#34;, []) if not vulns: if logger: logger(f&#34;No NVD data found for {cve_id}. Skipping.&#34;) return False, [], [] cve_info = vulns[0].get(&#34;cve&#34;, {}) if logger: logger(f&#34;Successfully retrieved NVD data for {cve_id}.&#34;) except Exception as e: if logger: logger(f&#34;Failed to fetch NVD data: {e}&#34;) return False, [], [] cisa_kev_data = {} mitre_cve_data = {} try: mitre_url = f&#34;[https://cveawg.mitre.org/api/cve/](https://cveawg.mitre.org/api/cve/){cve_id}&#34; mitre_response = requests.get(mitre_url) mitre_response.raise_for_status() mitre_cve_data = mitre_response.json() if logger: logger(f&#34;Successfully retrieved MITRE CVE data for {cve_id}.&#34;) except Exception as e: if logger: logger(f&#34;Failed to fetch MITRE CVE data: {e}&#34;) try: cisa_url = &#34;[https://www.cisa.gov/sites/default/files/feeds/known_exploited_vulnerabilities.json](https://www.cisa.gov/sites/default/files/feeds/known_exploited_vulnerabilities.json)&#34; cisa_response = requests.get(cisa_url) cisa_response.raise_for_status() cisa_kev_feed = cisa_response.json() cisa_kev_data[&#39;is_kev_exploited&#39;] = any( item[&#39;cveID&#39;] == cve_id for item in cisa_kev_feed.get(&#39;vulnerabilities&#39;, []) ) if logger: logger(f&#34;Successfully retrieved and searched CISA KEV data.&#34;) except Exception as e: if logger: logger(f&#34;Failed to fetch CISA KEV data: {e}&#34;) cisa_kev_data[&#39;is_kev_exploited&#39;] = False exploit_db_ids = fetch_exploit_db_id(cve_id, logger=logger) exploit_db_pocs = [] for eid in exploit_db_ids: poc = fetch_exploit_db_poc(eid, logger=logger) if poc: exploit_db_pocs.append((eid, poc)) poc_filename = os.path.join(DATA_DIR, f&#34;{cve_id}_exploit_{eid}.md&#34;) with open(poc_filename, &#34;w&#34;, encoding=&#34;utf-8&#34;) as f: f.write(f&#34;# Exploit DB PoC for {cve_id} - Exploit ID {eid}

